.shelf-container {
height: 200px;
position: relative;
}

.shelf-background {
position: absolute;
top: 0;
left: 0;
background-color: #eee;
width: calc(100% - 20px);
height: calc(100% - 20px);
border-left: solid 20px #ccc;
border-bottom: solid 20px #ddd;
z-index: 2;
}

.item-container {
padding: 10px 30px;
height: 180px;
overflow: hidden;
}

.item {
width: 135px;
height: 180px;
float: left;
position: relative;
margin-right: 20px;
}

.item a .front {
width: 100%;
height: 100%;
position: absolute;
transition: all 0.5s ease-in-out;
z-index: 10;
background-color: #44ff44;
backface-visibility: hidden;
background-size: cover;
}

.item:hover a .front {
transform: rotateY(180deg);
}

.item a .back {
width: calc(100% - 20px);
height: calc(100% - 20px);
position: absolute;
transition: all 0.5s ease-in-out;
z-index: 10;
background-color: #fff;
backface-visibility: hidden;
transform: rotateY(180deg);
color: black;
padding: 10px;
font-size: small;
color: #666;
}

.item:hover a .back {
transform: rotateY(360deg);
}

.item .shadow {
width: 135px;
height: 6px;
position: absolute;
background-color: black;
bottom: -3px;
left: 0;
z-index: 3;
filter: blur(10px);
transition: all 0.5s ease-in-out;
}

.item:hover .shadow {
transform: rotateY(180deg);
}
</style>

